import {AbstractControl, AsyncValidatorFn, FormBuilder, FormArray, FormControl, Validators} from '@angular/forms';

import { RxwebValidators,ReactiveFormConfig } from '../../../packages/reactive-form-validators';

(function() {
  describe('RxwebValidators', () => {
    beforeEach(() => {
      ReactiveFormConfig.set({
        "validationMessage": {
          "dataUri": "Please enter a valid data Uri",
        }
      });
    });

    describe('dataUri', () => {
      it('should not error on an empty string.',
        () => { 
          expect(RxwebValidators.dataUri()(new FormControl(''))).toBeNull();
        });
	
      it('should not error on null.',
        () => { 
          expect(RxwebValidators.dataUri()(new FormControl(null))).toBeNull();
        });
	
      it('should not error on undefined.',
        () => { 
          expect(RxwebValidators.dataUri()(new FormControl(undefined))).toBeNull(); 
        });

      it('should not give error on control which contains value which is proper dataUri.',
        () => { 
          expect(RxwebValidators.dataUri()(new FormControl('data:image/png;base64,'))).toBeNull(); 
        });

      it('should give error on control which contains value which is not proper dataUri.',
        () => { 
          expect(RxwebValidators.dataUri()(new FormControl('/png;base64,'))).toEqual({'dataUri':{ message: 'Please enter a valid data Uri', refValues: [ '/png;base64,' ] } }); 
        });


	      it('should not give error if the scheme value is DataUri and there is valid value in cssDataUri FormControl  with ConditionExpression with Function.',
        () => {
          let formBuilder = new FormBuilder();
          let formGroup = formBuilder.group({
            'scheme':['DataUri'],
            'cssDataUri':['data:image/png;base64,'],
          });
          expect(RxwebValidators.dataUri({conditionalExpression:(x,y) => x.scheme == "DataUri" })(formGroup.controls.cssDataUri)).toBeNull();

        });

	      it('should not give error if the scheme value is DataUri and there is valid value in javascriptDataUri FormControl with ConditionExpression with String.',
        () => {
          let formBuilder = new FormBuilder();
          let formGroup = formBuilder.group({
            'scheme':['DataUri'],
            'javascriptDataUri':['data:image/png;base64,'],
          });
          expect(RxwebValidators.dataUri({conditionalExpression:'x => x.scheme =="DataUri"'})(formGroup.controls.javascriptDataUri)).toBeNull();

        });

	      it('should not give error if the scheme value is html and there is invalid value in cssDataUri FormControl with ConditionExpression with Function.',
        () => {
          let formBuilder = new FormBuilder();
          let formGroup = formBuilder.group({
            'scheme':['html'],
            'cssDataUri':['/png;base64,'],
          });
          expect(RxwebValidators.dataUri({conditionalExpression:(x,y) => x.scheme == "DataUri" })(formGroup.controls.cssDataUri)).toBeNull();

        });

	      it('should not give error if the scheme value is html and there is invalid value in javascriptDataUri FormControl with ConditionExpression with String.',
        () => {
          let formBuilder = new FormBuilder();
          let formGroup = formBuilder.group({
            'scheme':['html'],
            'javascriptDataUri':['Udz@'],
          });
          expect(RxwebValidators.dataUri({conditionalExpression:'x => x.scheme =="DataUri"'})(formGroup.controls.javascriptDataUri)).toBeNull();

        });


	      it('should give error if the scheme value is DataUri and there is invalid value in cssDataUri FormControl with ConditionExpression with Function.',
        () => {
          let formBuilder = new FormBuilder();
          let formGroup = formBuilder.group({
            'scheme':['DataUri'],
            'cssDataUri':['/png;base64,'],
          });
          expect(RxwebValidators.dataUri({conditionalExpression:(x,y) => x.scheme == "DataUri" })(formGroup.controls.cssDataUri)).toEqual({'dataUri':{ message: 'Please enter a valid data Uri', refValues: [ '/png;base64,' ] } });

        });

	      it('should give error if the scheme value is DataUri and there is invalid value in javascriptDataUri FormControl with ConditionExpression with String.',
        () => {
          let formBuilder = new FormBuilder();
          let formGroup = formBuilder.group({
            'scheme':['DataUri'],
            'javascriptDataUri':['/png;base64,'],
          });
          expect(RxwebValidators.dataUri({conditionalExpression:'x => x.scheme =="DataUri"'})(formGroup.controls.javascriptDataUri)).toEqual({'dataUri':{ message: 'Please enter a valid data Uri', refValues: [ '/png;base64,' ] } });

        });




	      it('should give error, if the control contains value which is non proper dataUri.',
        () => {
          expect(RxwebValidators.dataUri({message:'{{0}} is not a proper data URI'})(new FormControl('/png;base64,'))).toEqual({'dataUri':{ message: '/png;base64, is not a proper data URI', refValues: [ '/png;base64,' ] } });

        });



//end
    });
  });
})();
